window.addEventListener("load", function(){
    window.cookieconsent.initialise({
        "container": document.getElementById("cookie-container"),
        "palette": {
            "popup": {
                "background": "#d5dadd",
                "text": "#5f676d"
            },
            "button": {
                "background": "#f67878",
                "text": "#ffffff"
            }
        },
        "theme": "classic",
        "type": "opt-out",
        "content": {
            "message": "Um unsere Webseite für Sie optimal zu gestalten und fortlaufend verbessern zu können, verwenden wir Cookies. Durch die weitere Nutzung der Webseite stimmen Sie der Verwendung von Cookies zu.\nWeitere Informationen zu Cookies erhalten Sie in unserer",
            "deny": "Ablehnen",
            "allow": "Akzeptieren",
            "link": "Datenschutzerklärung",
            "href": "/privacy.html",
            "policy" : "Cookie Einstellungen"
        },
        "revokable" : true,
        "revokeBtn": '<div id="revoke-test" class="cc-revoke {{classes}}">{{policy}}</div>',
        onStatusChange: function(status, chosenBefore) {
            var type = this.options.type;
            var didConsent = this.hasConsented();
            if (type == 'opt-out' && !didConsent) {
                // disable cookies
                gaOptout();
            }
            if (type == 'opt-out' && didConsent) {
                // enable cookies
                gaOptin();
            }
        }
})});